                if service == 'Employee Details':
                    with use_scope('result', clear=True):
                        put_image('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT-c2wGucNGwp8ex5acS7N4Tk0QOJMI5wV_tw&usqp=CAU', width='100%', height='90%')
                        put_markdown('## Employee Details')
                        cur.execute("SELECT emp_id, fname, lname, gender, dob, address, dno, dname FROM employee, department where employee.dno = department.dnum")
                        list = [['Emp id','First Name','Last Name','Gender','DOB','Address','Dept No','Dept Name']]
                        for(emp_id,fname,lname,gender,dob,address,dno,dname) in cur:
                            list.append([emp_id,fname,lname,gender,dob,address,dno,dname])
                        put_table(list)

                elif service == 'Attendance Report':
                    with use_scope('result', clear=True):
                        put_image('https://www.shutterstock.com/image-photo/attendance-report-text-on-white-260nw-1802622817.jpg', width='100%', height='90%')
                        put_markdown('## Attendance Register')
                        date = input('Date :')
                        cur.execute("SELECT emp_id, date, log_in, log_out FROM attendance where date = '{}'".format(date))
                        list = [['Emp id','Date','Login Time','Logout Time']]
                        for(emp_id,date,log_in,log_out) in cur:
                            list.append([emp_id,date,log_in,log_out])
                        put_table(list)
                
                elif service == 'Salary Report':
                    with use_scope('result', clear=True):
                        put_image('https://labourlawadvisor.in/blog/wp-content/uploads/2021/04/Horizontal-Thumbnail-017-696x392.jpg', width='100%', height='90%')
                        put_markdown('## Salary Report')
                        sal = [['Emp ID','Base Salary','Allowance','Increment','No of PL','Leaves taken','Reduction/day (if exceeding PL)','Credits']]
                        cur.execute("SELECT E_id, base_sal, allowance, increment, no_of_PL, leave_taken, reduction, credits FROM salary")
                        for (E_id,base_sal,allowance,increment,no_of_PL,leave_taken,reduction,credits) in cur:
                            sal.append([E_id,base_sal,allowance,increment,no_of_PL,leave_taken,reduction,credits])
                        put_table(sal)
                
                elif service == 'Add new Employee':
                    with use_scope('result', clear=True):
                        put_image('https://factorialhr.com/wp-content/uploads/2019/12/12132801/recruitment-process1.jpeg', width='100%', height='90%')
                        put_markdown('## Add New Employee')
                        n_fname = input('First Name :')
                        n_lname = input('Last Name :')
                        n_dob = input('DOB(YYYY-MM-DD) :')
                        n_gender = input('Gender :')
                        cur.execute("SELECT emp_id,fname FROM employee")
                        elist = [['Emp ID']]
                        for (emp_id,fname) in cur:
                            elist.append([emp_id])
                        put_table(elist)
                        n_emp_id = input('Employee ID(NOT in Table) :')
                        n_address = input('Address :')
                        cur.execute("SELECT dname, dnum FROM department")
                        dlist = []
                        for (dname,dnum) in cur:
                            dlist.append([dname,dnum])
                        n_dno = select("Select Department",dlist)
                        put_text(n_emp_id,n_dno)
